import React from "react";
import './style.css';

function Book(props) {
  return (
    <div>
      <a href={props.link}>
        <h3>Title: {props.title}</h3>
      </a>
      <h4>{ props.subtitle }</h4>
      <h6>Authors: 
      {props.authors.map(author => {
        return (
          <span key={author}> {author}</span>
        );
      })}
      </h6>
      <h6>Description: {props.description}</h6>
      <img src={props.image} alt={props.title}></img>
    </div>
  );
}

export default Book;
