import React from "react";
import './style.css';

function Book(props) {
  return (
    <div>
      <a href={props.link}>
        <h3>Title: {props.title}</h3>
      </a>

      {props.authors.map(author => {
        return (
          <h6 key={author}>Authors: {author}</h6>
        );
      })}
      <h6>Description: {props.description}</h6>
      <img src={props.image} alt={props.title}></img>
    </div>
  );
}

export default Book;
