import React from "react";
import './style.css';

function Book(props) {
  return (
    <div class="card mb-3" style="max-width: 540px;">
      <div class="row no-gutters">
        <div class="col-md-4">
          <img src={props.image} alt={props.title}></img>
        </div>
        <div class="col-md-8">
          <div class="card-body">
            <h5 class="card-title">Title: {props.title}</h5>
            <h6>{ props.subtitle }</h6>
            <h6 class="card-text">Authors: 
              {props.authors.map(author => {
                return (
                  <span key={author}> {author}</span>
                );
              })}
            </h6>
            <h6 class="card-text">Description: {props.description}</h6>
            <a href={props.link}>
              <button>View</button>
            </a>
            <button onClick={() => props.handleClick(props)}>{ props.clickType } </button>
          </div>
        </div>
      </div>
     </div>
  );
}

export default Book;
